import React, { useEffect, useRef, useState } from "react";
import { Html5Qrcode } from "html5-qrcode";

const GOOGLE_SCRIPT_URL = "https://script.google.com/macros/s/AKfycbw6YrLGud5yEWD_0j-E5JdyFTegq00FIwsX4Gtjxz9MKbXuWn6OuHV7wXdgfrl3rkbYPA/exec";

export default function QRScannerCameraApp() {
  const [status, setStatus] = useState("");
  const [log, setLog] = useState([]);
  const [showSuccess, setShowSuccess] = useState(false);
  const qrCodeRegionId = "reader";
  const html5QrCodeRef = useRef(null);
  const lastScannedRef = useRef(null);

  const handleScanSuccess = async (decodedText) => {
    if (decodedText === lastScannedRef.current) return; // Prevent duplicate
    lastScannedRef.current = decodedText;

    try {
      await fetch(GOOGLE_SCRIPT_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ scannedId: decodedText })
      });

      const newLog = {
        id: decodedText,
        result: "SENT",
        time: new Date().toLocaleTimeString()
      };
      setLog((prev) => [...prev, newLog]);
      setStatus("Submitted");
      setShowSuccess(true);

      // ✅ Stop scanning
      await html5QrCodeRef.current.stop();
      await html5QrCodeRef.current.clear();

      // ✅ Hide success animation after 3s
      setTimeout(() => {
        setShowSuccess(false);
        setStatus("");
      }, 3000);
    } catch (err) {
      setStatus("Error");
    }
  };

  useEffect(() => {
    html5QrCodeRef.current = new Html5Qrcode(qrCodeRegionId);
    Html5Qrcode.getCameras().then((devices) => {
      if (devices && devices.length) {
        html5QrCodeRef.current.start(
          { facingMode: "environment" },
          { fps: 10, qrbox: 250 },
          handleScanSuccess
        );
      }
    });

    return () => {
      html5QrCodeRef.current?.stop().then(() => {
        html5QrCodeRef.current.clear();
      });
    };
  }, []);

  return (
    <div style={{ textAlign: "center", fontFamily: "Arial" }}>
      <h2>Scan QR Code</h2>
      <div id={qrCodeRegionId} style={{ width: "100%", maxWidth: 400, margin: "auto" }}></div>

      {showSuccess && (
        <div style={{
          marginTop: 20,
          fontSize: "4rem",
          color: "green",
          animation: "pop 0.5s ease-out"
        }}>
          ✅
        </div>
      )}

      <p>Status: {status}</p>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {log.map((entry, i) => (
          <li key={i}>{entry.time} - {entry.id} - {entry.result}</li>
        ))}
      </ul>

      <style>
        {`
          @keyframes pop {
            0% { transform: scale(0.5); opacity: 0; }
            100% { transform: scale(1); opacity: 1; }
          }
        `}
      </style>
    </div>
  );
}
