import React, { useEffect, useRef, useState } from "react";
import { Html5Qrcode } from "html5-qrcode";

const GOOGLE_SCRIPT_URL =
  "https://script.google.com/macros/s/AKfycbxg9ya2e1Pf3vAMSGzwgSex0MtA86IU8YVQ5ufLGhGT-LwnkueU56huPiCoenuJVlNYHg/exec";

export default function QRScannerCameraApp() {
  const [status, setStatus] = useState("");
  const [log, setLog] = useState([]);
  const [showSuccess, setShowSuccess] = useState(false);
  const qrCodeRegionId = "reader";
  const html5QrCodeRef = useRef(null);
  const isProcessingRef = useRef(false); // ðŸš¨ This prevents duplicate scans

  const handleScanSuccess = async (decodedText) => {
    if (isProcessingRef.current) return; // ðŸ” Prevent duplicate triggers
    isProcessingRef.current = true;

    // Check for duplicate
    if (log.some((entry) => entry.id === decodedText)) {
      setStatus("Already Scanned");
      isProcessingRef.current = false;
      return;
    }

    try {
      await fetch(GOOGLE_SCRIPT_URL, {
        method: "POST",
        mode: "no-cors",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ scannedId: decodedText }),
      });

      const newLog = {
        id: decodedText,
        result: "SENT",
        time: new Date().toLocaleTimeString(),
      };
      setLog((prev) => [...prev, newLog]);
      setStatus("Submitted");
      setShowSuccess(true);

      // Stop and clear camera temporarily
      await html5QrCodeRef.current.stop();
      await html5QrCodeRef.current.clear();

      // Restart scanner after delay
      setTimeout(() => {
        setShowSuccess(false);
        setStatus("");
        isProcessingRef.current = false;

        Html5Qrcode.getCameras().then((devices) => {
          if (devices && devices.length) {
            html5QrCodeRef.current.start(
              { facingMode: "environment" },
              { fps: 10, qrbox: 250 },
              handleScanSuccess
            );
          }
        });
      }, 3000);
    } catch (err) {
      setStatus("Error");
      isProcessingRef.current = false;
    }
  };

  useEffect(() => {
    html5QrCodeRef.current = new Html5Qrcode(qrCodeRegionId);
    Html5Qrcode.getCameras().then((devices) => {
      if (devices && devices.length) {
        html5QrCodeRef.current.start(
          { facingMode: "environment" },
          { fps: 10, qrbox: 250 },
          handleScanSuccess
        );
      }
    });

    return () => {
      html5QrCodeRef.current?.stop().then(() => {
        html5QrCodeRef.current.clear();
      });
    };
  }, []);

  return (
    <div style={{ textAlign: "center" }}>
      <h2>Scan QR Code</h2>
      <div id={qrCodeRegionId} style={{ width: "100%", maxWidth: 400, margin: "auto" }}></div>

      {showSuccess && (
        <div
          style={{
            marginTop: 20,
            fontSize: "4rem",
            color: "green",
            animation: "pop 0.4s ease-out",
          }}
        >
          âœ…
        </div>
      )}

      <p>Status: {status}</p>
      <ul>
        {log.map((entry, i) => (
          <li key={i}>
            {entry.time} - {entry.id} - {entry.result}
          </li>
        ))}
      </ul>

      <style>
        {`
          @keyframes pop {
            0% { transform: scale(0.5); opacity: 0; }
            100% { transform: scale(1); opacity: 1; }
          }
        `}
      </style>
    </div>
  );
}
